package movies;import util.Input;public class MoviesApplication {    private static Input input = new Input();    public static Movie[] movies = MoviesArray.findAll();    public static void main(String[] args) {        String keepGoing;        do {            System.out.println("\n-----------------------------------\n");            System.out.print(                    "What would you like to do?\n" +                            "0 - Get me outta here\n" +                            "1 - view all movies\n" +                            "2 - view movies in the Animated category\n" +                            "3 - view movies in the Drama category\n" +                            "4 - view movies in the Horror category\n" +                            "5 - view movies in the SciFi category\n" +                            "Enter your choice - "            );            String choice = input.getString();            keepGoing = showMovies(choice);        } while (keepGoing.equals("y") || keepGoing.equals("yes"));    }    public static String showMovies(String userChoice) {        for (Movie movie : movies) {            String name = movie.getName();            String category = movie.getCategory();            if (userChoice.equals("1")) {                System.out.printf("%s --- %s\n", name, category);            } else if (userChoice.equals("2") && category.equals("animated")) {                System.out.printf("%s --- %s\n", name, category);            } else if (userChoice.equals("3") && category.equals("drama")) {                System.out.printf("%s --- %s\n", name, category);            } else if (userChoice.equals("4") && category.equals("horror")) {                System.out.printf("%s --- %s\n", name, category);            } else if (userChoice.equals("5") && category.equals("scifi")) {                System.out.printf("%s --- %s\n", name, category);            }        }        System.out.println("\n-----------------------------------\n");        System.out.print("Would you like to see another category? [y/n]: ");        return input.getString();    }}////////        boolean exit = false;////        Movie[] movies = MoviesArray.findAll();////        while (!exit) {//            System.out.println("\n-----------------------------------\n");//            System.out.print(//                    "What would you like to do?\n" +//                            "0 - Get me outta here\n" +//                            "1 - view all movies\n" +//                            "2 - view movies in the Animated category\n" +//                            "3 - view movies in the Drama category\n" +//                            "4 - view movies in the Horror category\n" +//                            "5 - view movies in the SciFi category\n" +//                            "Enter your choice - "//            );//            int userChoice = input.getInt(0, 5);////            if (userChoice == 0) {//                exit = true;//            } else if (userChoice == 1) {//                for (int i = 0; i < movies.length; i++){//                    System.out.println(movies[i].getName() + " -- " + movies[i].getCategory());//                }//            } else if (userChoice == 2) {//                for (int i = 0; i < movies.length; i++){//                    if(movies[i].getCategory().equals("animated")) {////                        System.out.printf("Here is your server name:%n%s-%s", randomAdjective, randomNouns);//                        System.out.println(movies[i].getName() + " -- " + movies[i].getCategory());//                    }//                }//            } else if (userChoice == 3) {//                for (int i = 0; i < movies.length; i++){//                    if(movies[i].getCategory().equals("drama")) {//                        System.out.println(movies[i].getName() + " -- " + movies[i].getCategory());//                 